import React, { useState } from 'react';

function VideoUpload() {
  const [caption, setCaption] = useState('');  // State to hold the caption

  function handleUpload(event) {
    const file = event.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    fetch('http://localhost:5000/upload-video', {
        method: 'POST',
        body: formData,
    })
    .then(response => {
        if (!response.ok) {
            throw new Error('Network response was not ok ' + response.statusText);
        }
        return response.json().catch(err => {
            throw new Error('Invalid JSON: ' + err.message);
        });
    })
    .then(data => {
        console.log('Upload successful', data);
        setCaption(data.message);  // Ensure 'message' field is being accessed
    })
    .catch(error => {
        console.error('Error uploading file:', error);
        setCaption('Failed to upload file.');
    });




    console.log('Uploading file:', file.name);
  }

  return (
    <div>
      <h1>Upload Video</h1>
      <input type="file" accept="video/*" onChange={handleUpload} />
      {caption && <div className="caption">{caption}</div>}  // Display the caption
    </div>
  );
}

export default VideoUpload;
